import React, { useState } from "react";

import {
  Button,
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Col,
  Container,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Row
} from "reactstrap";

const colors = [
  {
    name: "Primary",
    value: "primary"
  },
  {
    name: "Success",
    value: "success"
  },
  {
    name: "Danger",
    value: "danger"
  },
  {
    name: "Warning",
    value: "warning"
  }
];

const sizes = [
  {
    name: "Small",
    value: "sm"
  },
  {
    name: "Medium",
    value: "md"
  },
  {
    name: "Large",
    value: "lg"
  }
];

const DefaultModal = () => {
  const initOpenModals = () => {
    let modals = {};

    colors.forEach((color, index) => {
      modals = Object.assign({}, modals, {[index]: false})
    });

    return modals;
  };

  const [openModals, setOpenModals] = useState(() => initOpenModals());

  const toggle = index => {
    // Toggle selected element
    setOpenModals(openModals => Object.assign({}, openModals, {[index]: !openModals[index]}));
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle tag="h5">Default modal</CardTitle>
        <h6 className="card-subtitle text-muted">Default Bootstrap modal.</h6>
      </CardHeader>
      <CardBody className="text-center">
        <p>
          Use Bootstrap’s JavaScript modal plugin to add dialogs to your site
          for lightboxes, user notifications, or completely custom content.
        </p>

        {colors.map((color, index) => (
          <React.Fragment key={index}>
            <Button
              color={color.value}
              onClick={() => toggle(index)}
              className="mr-1"
            >
              {color.name}
            </Button>
